os_flavour_type=`hostnamectl | grep "Operating System" | awk '{print $3}'`; oracle_pkg(){ packages=("wow" "lsof" "sysstat" "virt-what" "dmidecode" "bc" "attr" "e2fsprogs" "fdisk" "gzip" "iproute2" "tar" "libxml2-devel" "brutils-2.5" ); for pkg in ${packages[@]}; do check=`yum -q list installed $pkg 2>&1 /dev/null`; if [ $? -ne 0 ]; then echo -e "${pkg} not_installed."; else echo -e  "${pkg} is_installed.";fi done }; case $os_flavour_type in "Ubuntu") echo -e "In progress";; "CentOS") oracle_pkg ;; "SUSE") oracle_pkg ;; "Red") oracle_pkg ;; "Oracle") oracle_pkg ;; "Amazon") oracle_pkg ;; *) echo -e "OS Type not detected ";; esac